$(document).ready(()=>{$("#testButton").on("click",()=>{const[s,e]=[$("#regexInput").val().trim(),$("#testString").val().trim()];let t="No se encontraron coincidencias";try{const n=new RegExp(s);if($("#regexInput").toggleClass("is-invalid",!s),$("#testString").toggleClass("is-invalid",!e),s&&e){const s=e.match(n);t=s?`Coincidencias: ${s.map(s=>`<span class="badge bg-success">${s}</span>`).join(", ")}`:t}}catch(s){$("#regexInput").addClass("is-invalid"),t="Expresión regular no válida"}$("#result").html(t).toggleClass("text-danger",t.includes("no válida")).toggleClass("text-success",t.includes("Coincidencias"))})});